(function() {

	var mongo = require('mongodb');
	var Saver = require('./Saver');

	var saver = Saver.getSavers();

	var handler = function() {};

	// @param data = {
	// 		urls : ['url', 'url']
	// }

	// @param callback(err, result, fin){}
	// @@param result = [
	// 			{
	// 				urls : ['url', 'url']
	// 			}
	// 	   ]
	handler.prototype.jingdong = function(data, callback, finish) {
		var saveTaskItem = {
			tasks : [{
				handler : 'jingdong',
				urls : data.urls
			}]			
		};
		saver['tasks'].push(saveTaskItem);
		callback(true, [data]);
		finish();
	};

	handler.prototype.baidu = function(data, callback, finish) {
		saver['baidu'].push(data);
		callback(true,[{urls:['http://www.baidu.com/']}]);
		finish();
	};

	exports.handler = handler;

}());
